mapper.update:
update board_${b_name}
set hit = hit + 1
where bno = #{bno}

update
	board_${b_name}
set 
	title = #{title}
	,content = #{content}
	,updatedate = now()
where 
	bno = #{bno}

update board_${b_name}
set
	replycnt = replycnt + #{amount}
where
	bno = #{bno}

update board_${b_name}
set
	likecnt = likecnt + #{amount}
where
	bno = #{bno}

update board_${b_name}
set
	dislikecnt = dislikecnt + #{amount}
where
	bno = #{bno}

mapper.select:
SELECT
	id
FROM
	board_${b_name}
WHERE
	bno = #{bno}
