mapper.insert:
INSERT INTO
	like_${b_name}
	(
		bno
		, id
		, flag
	)
VALUES
	(
		#{bno}
		, #{id}
		, #{flag}
	)

mapper.select:
SELECT
	count(*)
FROM
	like_${b_name}
WHERE
	id = #{id}
	AND bno = #{bno}

SELECT
	flag
FROM
	like_${b_name}
WHERE
	id = #{id}
	AND bno = #{bno}

mapper.delete:
DELETE FROM
	like_${b_name}
WHERE
	bno = #{bno}
	AND id = #{id}
