mapper.insert:
INSERT INTO
	music_basic
	(
		idx
		, song
		, singer
		, releasedate
		, composer
		, lyricist
		, arranger
		, copyright
		, secright
		, stockcnt
		, updatedate
	)
VALUES
	(
		#{idx}
		, #{song}
		, #{singer}
		, #{releaseDate}
		, #{composer}
		, #{lyricist}
		, #{arranger}
		, #{copyRight}
		, #{secRight}
		, #{stockCnt}
		, now()
	)

mapper.update:
UPDATE
	music_basic
SET
	recentprice = #{recentPrice}
	, recentpricetime = #{recentPriceTime}
	, buyunit = #{buyUnit}
	, buyprice = #{buyPrice}
	, sellunit = #{sellUnit}
	, sellprice = #{sellPrice}
	, updatedate = now()
WHERE
	idx = #{idx}

