mapper.insert:
INSERT INTO
	music_basic
	(
		idx
		, song
		, singer
		, releasedate
		, composer
		, lyricist
		, arranger
		, copyright
		, secright
		, stockcnt
	)
VALUES
	(
		#{idx}
		, #{song}
		, #{singer}
		, #{releaseDate}
		, #{composer}
		, #{lyricist}
		, #{arranger}
		, #{copyRight}
		, #{secRight}
		, #{stockCnt}
	)

