mapper.insert:
INSERT INTO
	reply_${b_name}
	(
		reply
		, regdate
		, updatedate
		, reply_step
		, bno
		, id
	)
VALUES
	(
		#{reply}
		, now()
		, now()
		, #{reply_step}
		, #{bno}
		, #{id}
	)

mapper.update:
UPDATE
	reply_${b_name}
SET
	reply = #{reply}
	, updatedate = now()
WHERE
	rno = #{rno}
