mapper.insert:
INSERT INTO
	reply_${b_name}
	(
		reply
		, regdate
		, updatedate
		, ref
		, reply_step
		, bno
		, id
	)
VALUES
	(
		#{reply}
		, now()
		, now()
		, #{rno}
		, #{ref}
		, #{bno}
		, #{id}
	)

mapper.select:
SELECT
	max(reply_step)
FROM
	reply_${b_name}
WHERE
	ref = #{rno}

mapper.update:
UPDATE
	reply_${b_name}
SET
	reply = #{reply}
	, updatedate = now()
WHERE
	rno = #{rno}

UPDATE
	reply_${b_name}
SET
	ref = #{ref}
WHERE
	rno = #{ref}
